mapper.select:
select * from ( select bbs_id ,title ,type ,open ,description ,is_password ,member_id ,regdate ,row_number() over (order by ${ord}) rn ,cont_uuid ,hits from n_bbs where 1=1
and ${searchCategory} like '%' || #{searchText} || '%'
and type = #{type}
and ${query}
order by ${ord} ) where rn between #{pagingVO.startRow} and #{pagingVO.nextPageRow}

        select count(*)
          from n_bbs
         where 1=1

      	   and ${searchCategory} like '%' || #{searchText} || '%'


      	   and type = #{type}


		   and ${query}

mapper.update:
update n_bbs
   set hits=hits+1
 where cont_uuid = #{cont_uuid}

mapper.select:
		select bbs_id
         where cont_uuid = #{cont_uuid}
         and password = #{password}

mapper.update:
update n_bbs
   set title=#{title,jdbcType=VARCHAR}
      ,type=#{type,jdbcType=VARCHAR}
      ,open=#{open,jdbcType=VARCHAR}
      ,description=#{description,jdbcType=VARCHAR}
      ,is_password=#{is_password,jdbcType=VARCHAR}
      ,member_id=#{member_id,jdbcType=VARCHAR}
      ,cont_uuid=#{cont_uuid,jdbcType=VARCHAR}
      ,password=#{password,jdbcType=VARCHAR}
 where cont_uuid=#{cont_uuid}
