mapper.insert:
insert into n_answers(
				  answers_id
				 ,subject_id
				 ,answers
				 ,member_id
				 ,poll_num
				 ,regdate
				 ,report_num
				 )
		   values(
		   		 ANSWERS_SEQ.NEXTVAL
		   		 ,#{subject_id ,jdbcType=VARCHAR}
		   		 ,#{answers ,jdbcType=VARCHAR}
		   		 ,#{member_id ,jdbcType=VARCHAR}
		   		 ,#{poll_num ,jdbcType=INTEGER}
		   		 ,sysdate
		   		 ,#{report_num, jdbcType=INTEGER}
		   		 )

mapper.select:
select answers_id ,subject_id ,questions ,answers ,member_id ,temporary ,regdate ,report_num from n_answers where subject_id = #{subject_id} and report_num = #{report_num}
and member_id = '${member_id}'
and ${query}
and answers_id = '${answers_id}'

select * from ( select count(*) cnt from ( select (select M001_NM from hisip.vw_mem001_m_monitor@sip2 t where M001_ID = a.member_id) member_name , m.team_num team_num from n_answers a, n_monitors m where a.member_id = m.member_id and a.subject_id = m.subject_id and a.poll_num=m.poll_num
and m.team_num = '${team_num}'
and a.subject_id = ${subject_id}
and a.member_id = '${member_id}'
and a.temporary = '${temporary}'
and a.member_name like '%' || #{searchText} || '%'
and a.member_id like '%' || #{searchText} || '%'
)b where 1=1
) where 1=1

select * from ( select c.*,row_number() over (order by regdate DESC ) rn from ( select a.answers_id ,a.subject_id ,a.questions ,a.answers ,a.member_id ,a.temporary ,a.regdate ,a.poll_num ,a.report_num ,(select team_num from n_monitors b where b.member_id = a.member_id AND b.subject_id = #{subject_id} ) team_num ,(select M001_NM from hisip.vw_mem001_m_monitor@sip2 t where M001_ID = a.member_id) member_name from n_answers a, n_monitors m where a.member_id = m.member_id and a.subject_id = m.subject_id and a.poll_num=m.poll_num and a.subject_id = #{subject_id}
and m.team_num = '${team_num}'
and a.temporary = '${temporary}'
order by regdate ASC )c where 1=1
and member_id = '${member_id}'
and member_name like '%' || #{searchText} || '%'
and member_id like '%' || #{searchText} || '%'
) where rn between #{pagingVO.startRow} and #{pagingVO.nextPageRow}

mapper.delete:
delete
  from n_answers
 where answers_id=#{answers_id} and report_num=#{report_num }

mapper.update:
update n_answers
   set answers=#{answers,jdbcType=VARCHAR}
      ,temporary=#{temporary,jdbcType=VARCHAR }
      ,regdate=sysdate
 where answers_id=#{answers_id} and report_num=#{report_num }
