mapper.insert:
insert into n_monitors(
				  idx
  				 ,member_id
  				 ,description
				 ,subject_id
				 ,team_num
				 ,poll_num
				 ,regdate)
		   values(
		   		 N_MONITORS_SEQ.NEXTVAL
		   		 ,#{member_id ,jdbcType=VARCHAR}
		   		 ,#{description ,jdbcType=VARCHAR }
		   		 ,#{subject_id ,jdbcType=VARCHAR}
		   		 ,#{team_num , jdbcType=INTEGER}
		   		 ,#{poll_num , jdbcType=INTEGER}
		   		 ,sysdate)

mapper.select:
select idx ,member_id ,subject_id ,description ,team_num ,regdate ,is_selection from n_monitors where subject_id = #{subject_id}
and member_id = #{member_id}
and ${query}

select * from ( select b.idx, b.member_id, b.subject_id, to_char(rn1) rn , b.description, b.team_num , b.poll_num, b.is_selection , b.apply_cnt, b.selected_cnt, b.regdate ,(select M001_NM from hisip.vw_mem001_m_monitor@sip2 where M001_ID = b.member_id) member_name ,(select job from member where id= b.member_id) job ,(select M001_SSN from hisip.vw_mem001_m_monitor@sip2 where M001_ID = b.member_id) birth ,(select M001_PHONE from hisip.vw_mem001_m_monitor@sip2 where M001_ID = b.member_id) phone ,(select sido from member where id= b.member_id ) sido ,(select gugun from member where id= b.member_id ) gugun ,(select dong from member where id= b.member_id ) dong ,(select tmp_sum from (select a.MEMBER_ID member_id, a.subject_id subject_id , sum(case when a.temporary = 'N' then 0 else 1 end) tmp_sum from N_ANSWERS a, ( select subject_id , MEMBER_ID from N_MONITORS where subject_id in (select subject_id from (select subject_id from( select subject_id, DENSE_RANK() OVER (ORDER BY subject_id desc) as rank from N_MONITORS where subject_id < #{subject_id} group by subject_id) where rank <= 3)) )b1 where a.subject_id = b1.subject_id and a.member_id = b1.member_id group by a.subject_id, a.member_id order by a.subject_id desc, a.member_id asc) where b.member_id = member_id and b.subject_ID = subject_id ) tmp_sum1 from (select A.idx, A.member_id, A.subject_id ,row_number() over (order by regdate asc) rn1 ,row_number() over (order by regdate desc ) rnd ,A.description, A.team_num, A.poll_num, A.is_selection ,(select COUNT(member_id) FROM n_monitors C where C.member_id = A.member_id ) apply_cnt ,(select COUNT(member_id) FROM n_monitors C where is_selection = 'Y' AND C.member_id = A.member_id )selected_cnt , A.regdate from n_monitors A where subject_id = #{subject_id}
)b ) where 1=1
and ${searchCategory} like '%' || #{searchText} || '%'
and team_num = #{team_num}
and is_selection = #{is_selection}

mapper.update:
update n_monitors
   set 
   team_num=#{team_num,jdbcType=INTEGER}
   ,description=#{description,jdbcType=VARCHAR}
 where idx=#{idx}

mapper.select:
        select count(*)
          from n_monitors
         where 1=1
			and subject_id = #{subject_id}

		
      	   and ${searchCategory} like '%' || #{searchText} || '%'
					
			
		
      	   and team_num = #{team_num}
		
		
      	   and is_selection = #{is_selection}
		

		
		   and ${query}

select count(*) from N_monitors m, n_subject s where m.member_id = #{member_id} AND m.IS_SELECTION = 'Y' AND s.subject_id = m.subject_id AND s.pay_YN = 'Y'
and ${searchCategory} like '%' || #{searchText} || '%'
and ${query}
