mapper.select:
select * from ( select A.idx ,A.member_id ,A.team ,row_number() over (order by ${ord}) rn ,A.bank ,A.bank_num ,A.subject_id ,(select pay_sum FROM n_subject B where B.subject_id = A.subject_id) pay ,A.regdate from n_monitors_apply A where 1=1
and ${query}
) where rn between #{pagingVO.startRow} and #{pagingVO.nextPageRow}

mapper.update:
update n_monitors_apply
   set bank=#{bank,jdbcType=VARCHAR}
      ,bank_num=#{bank_num,jdbcType=VARCHAR}
where idx = #{idx}

mapper.select:
select count(*) from n_monitors_apply where 1=1
and ${searchCategory} like '%' || #{searchText} || '%'
and ${query}
